mapper.select:
select count(*)
from w_activity act
inner join w_user u
on act.owner=u.id


        act.name like '%' #{name} '%'


        and u.username like '%' #{owner} '%'


        and act.type=#{type}


        and act.state=#{state}


        and act.startDate > #{startTime}


        and act.endDate < #{endTime}

select
act.id,u.username as owner,act.name,act.startDate,act.endDate,act.type,act.state,
act.budgetcost,act.actualcost,act.description,act.createTime,act.editBy,act.createBy,
act.editTime
from w_activity act
inner join w_user u
on act.owner=u.id


        act.name like '%' #{name} '%'


        and u.username like '%' #{owner} '%'


        and act.type= #{type}


        and act.state= #{state}


        and act.startDate > #{startTime}


        and act.endDate < #{endTime}


order by act.createTime desc
limit #{skipPageNum},#{pageSize}

mapper.delete:
delete from w_activity
where id in

    #{ids}
